'''Simulated competitor code for the ESPA-Comp.This code will take the same inputs and provide the same outputs as a competitor algorithm.'''import offer_utils as ouclass MakeOffer:    """Used to store status and forecast info and make an offer"""    def __init__(self):        pass        def make_me_an_offer(self):        # Find out the status of your resources:        # (state-of-charge, CPU time available, current surplus, market config)        self.status = ou.load_json('status')        self.times = ou.get_timeseries(self.status)                # Import the forecast data        self.demand = ou.get_latest_forecast(self.times,'demand')        self.renewables = ou.get_latest_forecast(self.times,'renewables')                # Compute and save your offer for each resource        for resource in self.status['resources']:            self.offer = ou.compute_offers(resource, self.status, self.times, self.demand,                                            self.renewables)            ou.save_offer(self.offer, resource, self.status['market_id'])        myoffer = MakeOffer()myoffer.make_me_an_offer()